"use strict";let clickAge=0,clickName=0,tableText;const students=[{firstName:"ali",age:30},{firstName:"reza",age:22},{firstName:"ahmad",age:41},{firstName:"leila",age:25}];function render(array){tableText="";for(let i=0;i<array.length;i++)tableText+=`<tr>\n                    <th scope="row"> ${i+1} </th>\n                    <td> ${students[i].firstName} </td>\n                    <td> ${students[i].age} </td> \n                    </tr>`;return tableText}function ageClicked(){let myClick;if(1==++clickAge%2){students.sort((function(a,b){return a.age-b.age}));let tableText=render(students);document.getElementById("sortAge").innerHTML=tableText,document.getElementById("ageHiddenTable").classList.remove("d-none")}else{students.sort((function(a,b){return b.age-a.age}));let tableText=render(students);document.getElementById("sortAge").innerHTML=tableText,document.getElementById("ageHiddenTable").classList.remove("d-none")}}function nameClicked(){let myClick;if(1==++clickName%2){students.sort((function(a,b){return a.firstName.toLocaleLowerCase()<b.firstName.toLocaleLowerCase()?-1:a.firstName.toLocaleLowerCase()>b.firstName.toLocaleLowerCase()?1:0}));let tableText=render(students);document.getElementById("sortName").innerHTML=tableText,document.getElementById("nameHiddenTable").classList.remove("d-none")}else{students.sort((function(a,b){return a.firstName.toLocaleLowerCase()>b.firstName.toLocaleLowerCase()?-1:a.firstName.toLocaleLowerCase()<b.firstName.toLocaleLowerCase()?1:0}));let tableText=render(students);document.getElementById("sortName").innerHTML=tableText,document.getElementById("nameHiddenTable").classList.remove("d-none")}}